//collect the Image data of the canvas
function generateRandomNumber() {
        //ajax request to get the random password generated by the python server
        $.ajax({
            type: "GET",
            url: "/generatePassword",
            contentType: 'application/json;charset=UTF-8',
            success: function (result) {
                //change result on webpage to prediction
                document.getElementById("result").innerHTML = result
            }
        });
    }
function passwordValidationTesting()
{
    var check = document.getElementById("validationTest").value
    $.ajax({
        type: "POST",
        url: "/checkPassword",
        contentType: 'application/json;charset=UTF-8',
        data: JSON.stringify({'password': check }),
        success: function (result) {
            //change result on webpage to prediction
            console.log(result)
        }
    });
}
